"use strict";(self.webpackChunk_jupyter_ai_jupyterlab_extension=self.webpackChunk_jupyter_ai_jupyterlab_extension||[]).push([[181],{1181(n,e,a){a(6704),a(2085);var o=a(5072),t=a.n(o),i=a(7825),r=a.n(i),p=a(7659),s=a.n(p),l=a(5056),c=a.n(l),d=a(540),g=a.n(d),j=a(1113),m=a.n(j),f=a(2646),b={};b.styleTagTransform=m(),b.setAttributes=c(),b.insert=s().bind(null,"head"),b.domAPI=r(),b.insertStyleElement=g(),t()(f.A,b),f.A&&f.A.locals&&f.A.locals},2646(n,e,a){a.d(e,{A:()=>p});var o=a(1601),t=a.n(o),i=a(6314),r=a.n(i)()(t());r.push([n.id,'/* ============================================\n * JUPYTER AI PANEL - Design System\n * Modern, minimal chat interface\n * ============================================ */\n\n/* ============================================\n * 1. DESIGN TOKENS\n * ============================================ */\n\n:root {\n  /* Spacing scale (4px base) */\n  --jp-ai-space-1: 4px;\n  --jp-ai-space-2: 8px;\n  --jp-ai-space-3: 12px;\n  --jp-ai-space-4: 16px;\n  --jp-ai-space-5: 24px;\n  --jp-ai-space-6: 32px;\n\n  /* Typography */\n  --jp-ai-font-size-xs: 11px;\n  --jp-ai-font-size-sm: 12px;\n  --jp-ai-font-size-base: 13px;\n  --jp-ai-font-size-md: 14px;\n  --jp-ai-font-size-lg: 15px;\n\n  /* Animation */\n  --jp-ai-ease: cubic-bezier(0.4, 0, 0.2, 1);\n  --jp-ai-duration-fast: 150ms;\n  --jp-ai-duration-normal: 200ms;\n  --jp-ai-duration-slow: 300ms;\n\n  /* Shadows */\n  --jp-ai-shadow-sm: 0 1px 2px rgba(0, 0, 0, 0.04);\n  --jp-ai-shadow-md: 0 2px 8px rgba(0, 0, 0, 0.08);\n\n  /* Border radius */\n  --jp-ai-radius-sm: 4px;\n  --jp-ai-radius-md: 8px;\n  --jp-ai-radius-lg: 12px;\n  --jp-ai-radius-full: 9999px;\n\n  /* Thinking theme - Amber */\n  --jp-ai-thinking-primary: #f59e0b;\n  --jp-ai-thinking-bg: rgba(245, 158, 11, 0.08);\n  --jp-ai-thinking-border: rgba(245, 158, 11, 0.2);\n\n  /* Tool theme - Cyan */\n  --jp-ai-tool-primary: #06b6d4;\n  --jp-ai-tool-bg: rgba(6, 182, 212, 0.08);\n  --jp-ai-tool-border: rgba(6, 182, 212, 0.2);\n\n  /* Error theme - Red */\n  --jp-ai-error-primary: #ef4444;\n  --jp-ai-error-bg: rgba(239, 68, 68, 0.08);\n  --jp-ai-error-border: rgba(239, 68, 68, 0.2);\n\n  /* Success theme - Green */\n  --jp-ai-success-primary: #22c55e;\n  --jp-ai-success-bg: rgba(34, 197, 94, 0.08);\n}\n\n/* Dark mode adjustments */\n[data-jp-theme-light="false"] {\n  --jp-ai-thinking-primary: #fbbf24;\n  --jp-ai-thinking-bg: rgba(251, 191, 36, 0.1);\n  --jp-ai-thinking-border: rgba(251, 191, 36, 0.25);\n\n  --jp-ai-tool-primary: #22d3ee;\n  --jp-ai-tool-bg: rgba(34, 211, 238, 0.1);\n  --jp-ai-tool-border: rgba(34, 211, 238, 0.25);\n\n  --jp-ai-error-primary: #f87171;\n  --jp-ai-error-bg: rgba(248, 113, 113, 0.1);\n\n  --jp-ai-success-primary: #4ade80;\n}\n\n/* ============================================\n * 2. ANIMATIONS\n * ============================================ */\n\n@keyframes jp-ai-fadeIn {\n  from {\n    opacity: 0;\n    transform: translateY(8px);\n  }\n  to {\n    opacity: 1;\n    transform: translateY(0);\n  }\n}\n\n@keyframes jp-ai-spin {\n  from { transform: rotate(0deg); }\n  to { transform: rotate(360deg); }\n}\n\n@keyframes jp-ai-pulse {\n  0%, 100% { opacity: 1; }\n  50% { opacity: 0.5; }\n}\n\n@keyframes jp-ai-cursorBlink {\n  0%, 100% { opacity: 1; }\n  50% { opacity: 0; }\n}\n\n/* ============================================\n * 3. PANEL CONTAINER\n * ============================================ */\n\n.jp-AIAgentPanel {\n  display: flex;\n  flex-direction: column;\n  height: 100%;\n  background: var(--jp-layout-color1);\n  color: var(--jp-ui-font-color1);\n  font-family: var(--jp-ui-font-family);\n  font-size: var(--jp-ai-font-size-md);\n  overflow: hidden;\n}\n\n#jp-right-stack > .jp-AIAgentPanel {\n  min-width: calc(var(--jp-sidebar-min-width, 320px) * 2);\n}\n\n.jp-AIAgentPanel-reactRoot {\n  display: flex;\n  flex-direction: column;\n  height: 100%;\n  flex: 1;\n  min-height: 0;\n  min-width: 0;\n}\n\n.jp-AIAgentPanel-container {\n  display: flex;\n  flex-direction: column;\n  height: 100%;\n}\n\n.jp-AIAgentPanel-conversationRoot {\n  display: flex;\n  flex-direction: column;\n  height: 100%;\n  flex: 1;\n  min-height: 0;\n  min-width: 0;\n}\n\n.jp-AIAgentPanel-conversationScroll {\n  min-height: 0;\n  flex: 1;\n  overflow-y: auto;\n}\n\n.jp-AIAgentPanel-composer {\n  flex-shrink: 0;\n  position: sticky;\n  bottom: 0;\n  z-index: 1;\n}\n\n/* ============================================\n * 4. HEADER\n * ============================================ */\n\n.jp-AIAgentPanel-header {\n  display: flex;\n  align-items: center;\n  justify-content: space-between;\n  padding: var(--jp-ai-space-3) var(--jp-ai-space-4);\n  border-bottom: 1px solid var(--jp-border-color3);\n  background: var(--jp-layout-color1);\n  flex-shrink: 0;\n}\n\n.jp-AIAgentPanel-header-left {\n  display: flex;\n  align-items: center;\n  gap: var(--jp-ai-space-3);\n}\n\n.jp-AIAgentPanel-header-right {\n  display: flex;\n  align-items: center;\n  gap: var(--jp-ai-space-2);\n}\n\n.jp-AIAgentPanel-seq {\n  font-size: var(--jp-ai-font-size-xs);\n  color: var(--jp-ui-font-color3);\n}\n\n.jp-AIAgentPanel-title {\n  font-size: var(--jp-ai-font-size-lg);\n  font-weight: 600;\n  color: var(--jp-ui-font-color0);\n}\n\n/* Status indicator */\n.jp-AIAgentPanel-status {\n  display: flex;\n  align-items: center;\n  gap: 6px;\n  font-size: var(--jp-ai-font-size-xs);\n  color: var(--jp-ui-font-color2);\n}\n\n.jp-AIAgentPanel-status-dot {\n  width: 8px;\n  height: 8px;\n  border-radius: 50%;\n  background: var(--jp-warn-color1);\n  transition: background var(--jp-ai-duration-fast) var(--jp-ai-ease);\n}\n\n.jp-AIAgentPanel-status-dot.connected {\n  background: var(--jp-success-color1);\n}\n\n.jp-AIAgentPanel-connectBtn {\n  padding: 4px 12px;\n  border: none;\n  border-radius: var(--jp-ai-radius-sm);\n  background: var(--jp-layout-color3);\n  color: var(--jp-ui-font-color1);\n  font-size: var(--jp-ai-font-size-xs);\n  font-weight: 500;\n  cursor: pointer;\n  transition: background var(--jp-ai-duration-fast) var(--jp-ai-ease);\n}\n\n.jp-AIAgentPanel-connectBtn:hover {\n  background: var(--jp-layout-color4);\n}\n\n.jp-AIAgentPanel-connectBtn:disabled {\n  opacity: 0.5;\n  cursor: not-allowed;\n}\n\n/* Model indicator */\n.jp-AIAgentPanel-modelIndicator {\n  font-size: var(--jp-ai-font-size-xs);\n  color: var(--jp-ui-font-color2);\n  padding: 2px 8px;\n  background: var(--jp-layout-color2);\n  border-radius: var(--jp-ai-radius-full);\n}\n\n/* ============================================\n * 5. MESSAGES AREA\n * ============================================ */\n\n.jp-AIAgentPanel-messages {\n  flex: 1;\n  overflow-y: auto;\n  padding: var(--jp-ai-space-4);\n  scroll-behavior: smooth;\n}\n\n/* Empty state */\n.jp-AIAgentPanel-empty {\n  display: flex;\n  flex-direction: column;\n  align-items: center;\n  justify-content: center;\n  height: 100%;\n  gap: var(--jp-ai-space-2);\n  color: var(--jp-ui-font-color3);\n}\n\n.jp-AIAgentPanel-empty-icon {\n  opacity: 0.3;\n}\n\n.jp-AIAgentPanel-empty-text {\n  font-size: var(--jp-ai-font-size-md);\n  font-weight: 500;\n}\n\n.jp-AIAgentPanel-empty-hint {\n  font-size: var(--jp-ai-font-size-sm);\n}\n\n/* ============================================\n * 6. MESSAGES\n * ============================================ */\n\n.jp-AIAgentPanel-message {\n  margin-bottom: var(--jp-ai-space-4);\n  animation: jp-ai-fadeIn var(--jp-ai-duration-normal) var(--jp-ai-ease);\n}\n\n.jp-AIAgentPanel-message-state {\n  font-size: var(--jp-ai-font-size-xs);\n  letter-spacing: 0.08em;\n  text-transform: uppercase;\n  color: var(--jp-ui-font-color2);\n  margin-bottom: var(--jp-ai-space-2);\n}\n\n.jp-AIAgentPanel-message-state--streaming {\n  color: var(--jp-ai-tool-primary);\n}\n\n.jp-AIAgentPanel-message-state--complete {\n  color: var(--jp-ai-success-primary);\n}\n\n.jp-AIAgentPanel-message-state--error {\n  color: var(--jp-ai-error-primary);\n}\n\n.jp-AIAgentPanel-message-role {\n  font-size: var(--jp-ai-font-size-xs);\n  font-weight: 500;\n  text-transform: uppercase;\n  letter-spacing: 0.05em;\n  color: var(--jp-ui-font-color2);\n  margin-bottom: var(--jp-ai-space-1);\n}\n\n.jp-AIAgentPanel-message-content {\n  font-size: var(--jp-ai-font-size-md);\n  line-height: 1.6;\n}\n\n/* User messages */\n.jp-AIAgentPanel-message--user {\n  border-left: 2px solid var(--jp-brand-color1);\n  padding: var(--jp-ai-space-2) var(--jp-ai-space-3);\n  margin-left: var(--jp-ai-space-4);\n}\n\n/* Assistant messages - minimal wrapper, no box */\n.jp-AIAgentPanel-message--assistant {\n  padding: var(--jp-ai-space-2) 0;\n}\n\n/* Answer content with markdown support */\n.jp-AIAgentPanel-answerContent {\n  font-size: var(--jp-ai-font-size-md);\n  line-height: 1.6;\n  word-wrap: break-word;\n}\n\n/* Streaming state - markdown updates without debounce */\n.jp-AIAgentPanel-answerContent--streaming {\n  font-family: inherit;\n  white-space: normal;\n}\n\n.jp-AIAgentPanel-markdownStream {\n  display: block;\n}\n\n.jp-AIAgentPanel-answerContent--streaming .jp-AIAgentPanel-markdown p:last-child {\n  margin-bottom: var(--jp-ai-space-3);\n}\n\n.jp-AIAgentPanel-answerContent--streaming .jp-AIAgentPanel-copyBtn {\n  display: none;\n}\n\n/* Markdown content styling */\n.jp-AIAgentPanel-markdown p {\n  margin: 0 0 var(--jp-ai-space-3) 0;\n}\n\n.jp-AIAgentPanel-markdown p:last-child {\n  margin-bottom: 0;\n}\n\n.jp-AIAgentPanel-markdown ul,\n.jp-AIAgentPanel-markdown ol {\n  margin: 0 0 var(--jp-ai-space-3) 0;\n  padding-left: var(--jp-ai-space-6);\n}\n\n.jp-AIAgentPanel-markdown li {\n  margin-bottom: var(--jp-ai-space-1);\n}\n\n.jp-AIAgentPanel-markdown h1,\n.jp-AIAgentPanel-markdown h2,\n.jp-AIAgentPanel-markdown h3,\n.jp-AIAgentPanel-markdown h4 {\n  margin: var(--jp-ai-space-4) 0 var(--jp-ai-space-2) 0;\n  font-weight: 600;\n  line-height: 1.3;\n}\n\n.jp-AIAgentPanel-markdown h1 {\n  font-size: 1.4em;\n}\n\n.jp-AIAgentPanel-markdown h2 {\n  font-size: 1.2em;\n}\n\n.jp-AIAgentPanel-markdown h3 {\n  font-size: 1.1em;\n}\n\n.jp-AIAgentPanel-markdown blockquote {\n  margin: 0 0 var(--jp-ai-space-3) 0;\n  padding-left: var(--jp-ai-space-3);\n  border-left: 3px solid var(--jp-border-color2);\n  color: var(--jp-ui-font-color2);\n}\n\n.jp-AIAgentPanel-markdown strong {\n  font-weight: 600;\n}\n\n.jp-AIAgentPanel-markdown a {\n  color: var(--jp-brand-color1);\n  text-decoration: none;\n}\n\n.jp-AIAgentPanel-markdown a:hover {\n  text-decoration: underline;\n}\n\n.jp-AIAgentPanel-thinkingMarkdown {\n  color: var(--jp-ui-font-color2);\n}\n\n/* Inline code */\n.jp-AIAgentPanel-inlineCode {\n  font-family: ui-monospace, SFMono-Regular, \'SF Mono\', Menlo, Consolas, monospace;\n  font-size: 0.9em;\n  padding: 2px 6px;\n  background: #f6f8fa;\n  border: 1px solid #e1e4e8;\n  border-radius: var(--jp-ai-radius-sm);\n  color: #8250df;\n}\n\n[data-jp-theme-light="false"] .jp-AIAgentPanel-inlineCode {\n  background: rgba(255, 255, 255, 0.08);\n  border-color: var(--jp-border-color2);\n  color: #d2a8ff;\n}\n\n/* Code block wrapper */\n.jp-AIAgentPanel-codeblock-wrapper {\n  margin: var(--jp-ai-space-3) 0;\n  border-radius: var(--jp-ai-radius-md);\n  overflow: hidden;\n  background: #f6f8fa;\n  border: 1px solid #e1e4e8;\n}\n\n[data-jp-theme-light="false"] .jp-AIAgentPanel-codeblock-wrapper {\n  background: var(--jp-layout-color2);\n  border-color: var(--jp-border-color2);\n}\n\n/* Gradient accent bar */\n.jp-AIAgentPanel-codeblock-gradient {\n  height: 3px;\n  background: linear-gradient(90deg, #0969da, #8250df);\n}\n\n/* Code block header */\n.jp-AIAgentPanel-codeblock-header {\n  display: flex;\n  align-items: center;\n  justify-content: space-between;\n  padding: var(--jp-ai-space-2) var(--jp-ai-space-3);\n  background: rgba(255, 255, 255, 0.6);\n  border-bottom: 1px solid #e1e4e8;\n}\n\n[data-jp-theme-light="false"] .jp-AIAgentPanel-codeblock-header {\n  background: rgba(255, 255, 255, 0.05);\n  border-bottom-color: var(--jp-border-color2);\n}\n\n/* Language label */\n.jp-AIAgentPanel-codeblock-lang {\n  font-size: 11px;\n  font-weight: 600;\n  color: #656d76;\n  text-transform: uppercase;\n  letter-spacing: 0.5px;\n}\n\n[data-jp-theme-light="false"] .jp-AIAgentPanel-codeblock-lang {\n  color: var(--jp-ui-font-color2);\n}\n\n/* Copy button */\n.jp-AIAgentPanel-copyBtn {\n  display: flex;\n  align-items: center;\n  justify-content: center;\n  padding: 4px;\n  background: transparent;\n  border: none;\n  border-radius: var(--jp-ai-radius-sm);\n  color: #656d76;\n  cursor: pointer;\n  transition: all 0.25s ease;\n}\n\n.jp-AIAgentPanel-copyBtn .jp-AIAgentPanel-copyIcon {\n  transition: all 0.25s ease;\n}\n\n.jp-AIAgentPanel-copyBtn:hover {\n  color: #0969da;\n}\n\n.jp-AIAgentPanel-copyBtn:hover .jp-AIAgentPanel-copyIcon {\n  transform: rotate(-5deg) scale(1.05);\n  filter: drop-shadow(0 0 3px rgba(9, 105, 218, 0.5));\n}\n\n.jp-AIAgentPanel-copyBtn--success {\n  color: #16a34a;\n}\n\n/* Code block pre/code */\n.jp-AIAgentPanel-codeblock {\n  margin: 0;\n  padding: 12px 16px;\n  overflow-x: auto;\n  background: #f6f8fa;\n}\n\n.jp-AIAgentPanel-codeblock code {\n  font-family: ui-monospace, SFMono-Regular, \'SF Mono\', Menlo, Consolas, monospace;\n  font-size: 13px;\n  line-height: 1.5;\n  color: #1f2328;\n  white-space: pre;\n}\n\n[data-jp-theme-light="false"] .jp-AIAgentPanel-codeblock {\n  background: rgba(0, 0, 0, 0.2);\n}\n\n[data-jp-theme-light="false"] .jp-AIAgentPanel-codeblock code {\n  color: var(--jp-content-font-color1);\n}\n\n/* Syntax highlighting - GitHub-inspired light theme */\n.jp-AIAgentPanel-codeblock .hljs {\n  color: #1f2328;\n}\n\n.jp-AIAgentPanel-codeblock .hljs-keyword,\n.jp-AIAgentPanel-codeblock .hljs-selector-tag,\n.jp-AIAgentPanel-codeblock .hljs-literal,\n.jp-AIAgentPanel-codeblock .hljs-section,\n.jp-AIAgentPanel-codeblock .hljs-link {\n  color: #cf222e;\n  font-weight: 500;\n}\n\n.jp-AIAgentPanel-codeblock .hljs-string,\n.jp-AIAgentPanel-codeblock .hljs-title,\n.jp-AIAgentPanel-codeblock .hljs-name,\n.jp-AIAgentPanel-codeblock .hljs-attribute,\n.jp-AIAgentPanel-codeblock .hljs-symbol,\n.jp-AIAgentPanel-codeblock .hljs-bullet,\n.jp-AIAgentPanel-codeblock .hljs-addition,\n.jp-AIAgentPanel-codeblock .hljs-template-tag,\n.jp-AIAgentPanel-codeblock .hljs-template-variable {\n  color: #0a3069;\n}\n\n.jp-AIAgentPanel-codeblock .hljs-type,\n.jp-AIAgentPanel-codeblock .hljs-variable,\n.jp-AIAgentPanel-codeblock .hljs-class {\n  color: #953800;\n}\n\n.jp-AIAgentPanel-codeblock .hljs-number,\n.jp-AIAgentPanel-codeblock .hljs-regexp,\n.jp-AIAgentPanel-codeblock .hljs-selector-id,\n.jp-AIAgentPanel-codeblock .hljs-selector-class {\n  color: #0550ae;\n}\n\n.jp-AIAgentPanel-codeblock .hljs-comment,\n.jp-AIAgentPanel-codeblock .hljs-quote,\n.jp-AIAgentPanel-codeblock .hljs-deletion,\n.jp-AIAgentPanel-codeblock .hljs-meta {\n  color: #6e7781;\n  font-style: italic;\n}\n\n.jp-AIAgentPanel-codeblock .hljs-built_in,\n.jp-AIAgentPanel-codeblock .hljs-doctag {\n  color: #0550ae;\n}\n\n.jp-AIAgentPanel-codeblock .hljs-attr,\n.jp-AIAgentPanel-codeblock .hljs-selector-attr,\n.jp-AIAgentPanel-codeblock .hljs-selector-pseudo,\n.jp-AIAgentPanel-codeblock .hljs-property {\n  color: #0550ae;\n}\n\n.jp-AIAgentPanel-codeblock .hljs-tag {\n  color: #116329;\n}\n\n.jp-AIAgentPanel-codeblock .hljs-function,\n.jp-AIAgentPanel-codeblock .hljs-title.function_ {\n  color: #8250df;\n}\n\n.jp-AIAgentPanel-codeblock .hljs-emphasis {\n  font-style: italic;\n}\n\n.jp-AIAgentPanel-codeblock .hljs-strong {\n  font-weight: bold;\n}\n\n/* Dark theme overrides */\n[data-jp-theme-light="false"] .jp-AIAgentPanel-codeblock .hljs {\n  color: #e6edf3;\n}\n\n[data-jp-theme-light="false"] .jp-AIAgentPanel-codeblock .hljs-keyword {\n  color: #ff7b72;\n}\n\n[data-jp-theme-light="false"] .jp-AIAgentPanel-codeblock .hljs-string {\n  color: #a5d6ff;\n}\n\n[data-jp-theme-light="false"] .jp-AIAgentPanel-codeblock .hljs-number {\n  color: #79c0ff;\n}\n\n[data-jp-theme-light="false"] .jp-AIAgentPanel-codeblock .hljs-comment {\n  color: #8b949e;\n}\n\n[data-jp-theme-light="false"] .jp-AIAgentPanel-codeblock .hljs-function,\n[data-jp-theme-light="false"] .jp-AIAgentPanel-codeblock .hljs-title.function_ {\n  color: #d2a8ff;\n}\n\n[data-jp-theme-light="false"] .jp-AIAgentPanel-codeblock .hljs-variable,\n[data-jp-theme-light="false"] .jp-AIAgentPanel-codeblock .hljs-type {\n  color: #ffa657;\n}\n\n/* ============================================\n * 7. THINKING BLOCK - Amber Theme\n * ============================================ */\n\n.jp-AIAgentPanel-thinkingBlock {\n  background: var(--jp-ai-thinking-bg);\n  border: 1px solid var(--jp-ai-thinking-border);\n  border-radius: var(--jp-ai-radius-md);\n  margin-bottom: var(--jp-ai-space-3);\n  overflow: hidden;\n  transition: opacity var(--jp-ai-duration-normal) var(--jp-ai-ease);\n}\n\n.jp-AIAgentPanel-thinkingBlock-header {\n  display: flex;\n  align-items: center;\n  justify-content: space-between;\n  padding: var(--jp-ai-space-2) var(--jp-ai-space-3);\n  cursor: pointer;\n  user-select: none;\n  transition: background var(--jp-ai-duration-fast) var(--jp-ai-ease);\n}\n\n.jp-AIAgentPanel-thinkingBlock-header:hover {\n  background: rgba(245, 158, 11, 0.05);\n}\n\n.jp-AIAgentPanel-thinkingBlock-header:focus {\n  outline: 2px solid var(--jp-ai-thinking-primary);\n  outline-offset: -2px;\n}\n\n.jp-AIAgentPanel-thinkingBlock-header-left {\n  display: flex;\n  align-items: center;\n  gap: var(--jp-ai-space-2);\n}\n\n.jp-AIAgentPanel-thinkingBlock-header-right {\n  display: flex;\n  align-items: center;\n  gap: var(--jp-ai-space-2);\n}\n\n.jp-AIAgentPanel-thinkingBlock-icon {\n  color: var(--jp-ai-thinking-primary);\n  display: flex;\n  align-items: center;\n}\n\n.jp-AIAgentPanel-thinkingBlock-label {\n  font-size: var(--jp-ai-font-size-sm);\n  font-weight: 500;\n  color: var(--jp-ai-thinking-primary);\n}\n\n.jp-AIAgentPanel-thinkingBlock-streaming {\n  font-size: var(--jp-ai-font-size-xs);\n  color: var(--jp-ai-thinking-primary);\n  opacity: 0.7;\n  animation: jp-ai-pulse 1.5s ease-in-out infinite;\n}\n\n/* Duration and Token badges */\n.jp-AIAgentPanel-thinkingBlock-badge {\n  display: inline-flex;\n  align-items: center;\n  gap: 4px;\n  font-size: var(--jp-ai-font-size-xs);\n  color: var(--jp-ai-thinking-primary);\n  opacity: 0.8;\n  padding: 2px 6px;\n  background: rgba(245, 158, 11, 0.1);\n  border-radius: var(--jp-ai-radius-sm);\n}\n\n.jp-AIAgentPanel-thinkingBlock-badge-value {\n  font-weight: 500;\n}\n\n.jp-AIAgentPanel-thinkingBlock-chevron {\n  color: var(--jp-ai-thinking-primary);\n  opacity: 0.6;\n  display: flex;\n  align-items: center;\n  transition: transform var(--jp-ai-duration-fast) var(--jp-ai-ease);\n}\n\n/* Content wrapper */\n.jp-AIAgentPanel-thinkingBlock-contentWrapper {\n  max-height: 300px;\n  overflow-y: auto;\n}\n\n.jp-AIAgentPanel-thinkingBlock-content {\n  padding: 0 var(--jp-ai-space-3) var(--jp-ai-space-3);\n}\n\n.jp-AIAgentPanel-thinkingBlock-text {\n  font-family: var(--jp-code-font-family);\n  font-size: var(--jp-ai-font-size-sm);\n  color: var(--jp-ui-font-color2);\n  line-height: 1.5;\n  white-space: pre-wrap;\n}\n\n/* Summary section (shown when collapsed) */\n.jp-AIAgentPanel-thinkingBlock-summary {\n  font-size: var(--jp-ai-font-size-sm);\n  color: var(--jp-ui-font-color2);\n  padding: 0 var(--jp-ai-space-3) var(--jp-ai-space-2);\n  font-style: italic;\n  opacity: 0.9;\n}\n\n/* Streaming state - glow effect */\n.jp-AIAgentPanel-thinkingBlock--streaming {\n  box-shadow: 0 0 8px rgba(245, 158, 11, 0.2);\n}\n\n/* Completed state */\n.jp-AIAgentPanel-thinkingBlock--completed {\n  opacity: 0.9;\n}\n\n.jp-AIAgentPanel-thinkingBlock--completed .jp-AIAgentPanel-thinkingBlock-header {\n  cursor: pointer;\n}\n\n/* Collapsed state */\n.jp-AIAgentPanel-thinkingBlock--collapsed .jp-AIAgentPanel-thinkingBlock-contentWrapper {\n  display: none;\n}\n\n/* Blinking cursor (for thinking block) */\n.jp-AIAgentPanel-cursor {\n  display: inline-block;\n  width: 2px;\n  height: 14px;\n  background: var(--jp-ai-thinking-primary);\n  margin-left: 2px;\n  animation: jp-ai-cursorBlink 0.8s ease-in-out infinite;\n  vertical-align: text-bottom;\n}\n\n/* ============================================\n * 8. EXECUTION GROUP - Tool Calls Container\n * ============================================ */\n\n.jp-ExecutionGroup {\n  margin: var(--jp-ai-space-3) 0;\n  display: flex;\n  background: linear-gradient(145deg, rgba(255,255,255,0.97), rgba(248,250,252,0.98));\n  border-radius: 10px;\n  overflow: hidden;\n  border: 1px solid rgba(0,0,0,0.07);\n  box-shadow: 0 2px 16px rgba(0,0,0,0.04);\n  animation: jp-ai-fadeIn 0.3s ease;\n}\n\n[data-jp-theme-light="false"] .jp-ExecutionGroup {\n  background: linear-gradient(145deg, rgba(30,30,40,0.97), rgba(25,25,35,0.98));\n  border-color: rgba(255,255,255,0.1);\n}\n\n/* Gradient left edge */\n.jp-ExecutionGroup-gradient {\n  width: 3px;\n  background: linear-gradient(180deg, #0969da, #8250df);\n  flex-shrink: 0;\n}\n\n.jp-ExecutionGroup-content {\n  flex: 1;\n  min-width: 0;\n}\n\n.jp-ExecutionGroup-header {\n  width: 100%;\n  padding: 9px 12px;\n  background: rgba(255,255,255,0.8);\n  border: none;\n  border-bottom: 1px solid rgba(0,0,0,0.05);\n  cursor: pointer;\n  display: flex;\n  align-items: center;\n  gap: 8px;\n  font-size: 11px;\n  font-family: inherit;\n  text-align: left;\n}\n\n[data-jp-theme-light="false"] .jp-ExecutionGroup-header {\n  background: rgba(255,255,255,0.05);\n  border-bottom-color: rgba(255,255,255,0.08);\n}\n\n.jp-ExecutionGroup-header:hover {\n  background: rgba(255,255,255,0.9);\n}\n\n[data-jp-theme-light="false"] .jp-ExecutionGroup-header:hover {\n  background: rgba(255,255,255,0.08);\n}\n\n.jp-ExecutionGroup-icon {\n  color: #0969da;\n  display: flex;\n  align-items: center;\n}\n\n.jp-ExecutionGroup-summary {\n  font-weight: 600;\n  color: #1f2328;\n}\n\n[data-jp-theme-light="false"] .jp-ExecutionGroup-summary {\n  color: #e6edf3;\n}\n\n.jp-ExecutionGroup-streamingDot {\n  width: 6px;\n  height: 6px;\n  border-radius: 50%;\n  background: #0969da;\n  opacity: 0;\n  transition: opacity 0.3s ease;\n}\n\n.jp-ExecutionGroup--executing .jp-ExecutionGroup-streamingDot {\n  opacity: 0.8;\n  animation: jp-ai-pulse 1.5s ease-in-out infinite;\n}\n\n.jp-ExecutionGroup-expandIcon {\n  margin-left: auto;\n  color: #656d76;\n  display: flex;\n  align-items: center;\n  transition: transform 0.25s ease;\n}\n\n.jp-ExecutionGroup--expanded .jp-ExecutionGroup-expandIcon {\n  transform: rotate(180deg);\n}\n\n/* History list (collapsible) */\n.jp-ExecutionGroup-list {\n  max-height: 200px;\n  overflow-y: auto;\n  transition: max-height 0.35s cubic-bezier(0.4, 0, 0.2, 1);\n}\n\n.jp-ExecutionGroup-list--collapsed {\n  max-height: 0;\n  overflow: hidden;\n}\n\n/* History item */\n.jp-ExecutionGroup-historyItem {\n  display: flex;\n  align-items: center;\n  gap: 8px;\n  padding: 6px 12px;\n  font-size: 11px;\n  border-bottom: 1px solid #eef0f2;\n  background: rgba(255,255,255,0.6);\n  animation: jp-ai-historySlide 0.3s cubic-bezier(0.4, 0, 0.2, 1);\n}\n\n[data-jp-theme-light="false"] .jp-ExecutionGroup-historyItem {\n  border-bottom-color: rgba(255,255,255,0.08);\n  background: rgba(255,255,255,0.03);\n}\n\n@keyframes jp-ai-historySlide {\n  from { opacity: 0; transform: translateY(-4px); }\n  to { opacity: 1; transform: translateY(0); }\n}\n\n.jp-ExecutionGroup-historyItem .jp-ExecutionGroup-item-dot {\n  width: 8px;\n  height: 8px;\n  background: #16a34a;\n}\n\n.jp-ExecutionGroup-historyItem .jp-ExecutionGroup-item-name {\n  color: #656d76;\n}\n\n.jp-ExecutionGroup-historyItem .jp-ExecutionGroup-item-check {\n  display: flex;\n  color: #16a34a;\n  font-size: 9px;\n}\n\n/* Latest action (pinned at bottom) */\n.jp-ExecutionGroup-latest {\n  border-top: 1px solid rgba(0,0,0,0.05);\n  overflow: hidden;\n  max-height: 200px;\n  transition: max-height 0.35s cubic-bezier(0.4, 0, 0.2, 1),\n              opacity 0.25s ease,\n              border-color 0.25s ease;\n}\n\n.jp-ExecutionGroup-latest--hidden {\n  max-height: 0;\n  opacity: 0;\n  border-top-color: transparent;\n}\n\n[data-jp-theme-light="false"] .jp-ExecutionGroup-latest {\n  border-top-color: rgba(255,255,255,0.08);\n}\n\n[data-jp-theme-light="false"] .jp-ExecutionGroup-latest--hidden {\n  border-top-color: transparent;\n}\n\n/* Tool item */\n.jp-ExecutionGroup-item {\n  display: flex;\n  align-items: center;\n  gap: 8px;\n  padding: 10px 12px;\n  font-size: 12px;\n  background: rgba(255,255,255,0.85);\n  transition: background 0.8s cubic-bezier(0.4, 0, 0.2, 1);\n}\n\n.jp-ExecutionGroup-item--running {\n  background: linear-gradient(90deg, rgba(254,243,199,0.95) 0%, rgba(255,251,235,0.9) 100%);\n}\n\n[data-jp-theme-light="false"] .jp-ExecutionGroup-item {\n  background: rgba(255,255,255,0.05);\n}\n\n[data-jp-theme-light="false"] .jp-ExecutionGroup-item--running {\n  background: linear-gradient(90deg, rgba(50,45,30,0.95) 0%, rgba(30,30,40,0.9) 100%);\n}\n\n/* Status dot */\n.jp-ExecutionGroup-item-dot {\n  width: 8px;\n  height: 8px;\n  border-radius: 50%;\n  background: #0969da;\n  flex-shrink: 0;\n  transition: background 0.3s ease, box-shadow 0.3s ease;\n}\n\n.jp-ExecutionGroup-item--running .jp-ExecutionGroup-item-dot {\n  box-shadow: 0 0 10px rgba(9, 105, 218, 0.35);\n}\n\n.jp-ExecutionGroup-item--success .jp-ExecutionGroup-item-dot {\n  background: #16a34a;\n  box-shadow: none;\n}\n\n.jp-ExecutionGroup-item--error .jp-ExecutionGroup-item-dot {\n  background: #cf222e;\n  box-shadow: none;\n}\n\n.jp-ExecutionGroup-item-name {\n  flex: 1;\n  font-weight: 500;\n  color: #1f2328;\n  overflow: hidden;\n  text-overflow: ellipsis;\n  white-space: nowrap;\n}\n\n[data-jp-theme-light="false"] .jp-ExecutionGroup-item-name {\n  color: #e6edf3;\n}\n\n/* Check icon */\n.jp-ExecutionGroup-item-check {\n  flex-shrink: 0;\n  color: #16a34a;\n  display: none;\n}\n\n.jp-ExecutionGroup-item-check--visible {\n  display: flex;\n  animation: jp-ai-checkFadeIn 0.3s cubic-bezier(0.4, 0, 0.2, 1);\n}\n\n@keyframes jp-ai-checkFadeIn {\n  from { opacity: 0; transform: scale(0.8); }\n  to { opacity: 1; transform: scale(1); }\n}\n\n.jp-ExecutionGroup-item-check svg {\n  width: 11px;\n  height: 11px;\n}\n\n/* Category badges */\n.jp-Category-badge {\n  display: inline-flex;\n  align-items: center;\n  justify-content: center;\n  padding: 2px 6px;\n  border-radius: 3px;\n  font-size: 9px;\n  font-weight: 700;\n  letter-spacing: 0.5px;\n  text-transform: uppercase;\n  flex-shrink: 0;\n  font-family: ui-monospace, SFMono-Regular, monospace;\n}\n\n.jp-Category-badge--read {\n  background: #ddf4ff;\n  color: #0969da;\n}\n\n.jp-Category-badge--write {\n  background: #fff8c5;\n  color: #9a6700;\n}\n\n.jp-Category-badge--execute {\n  background: #dcfce7;\n  color: #16a34a;\n}\n\n[data-jp-theme-light="false"] .jp-Category-badge--read {\n  background: rgba(9, 105, 218, 0.2);\n  color: #58a6ff;\n}\n\n[data-jp-theme-light="false"] .jp-Category-badge--write {\n  background: rgba(154, 103, 0, 0.2);\n  color: #d29922;\n}\n\n[data-jp-theme-light="false"] .jp-Category-badge--execute {\n  background: rgba(22, 163, 74, 0.2);\n  color: #3fb950;\n}\n\n/* ============================================\n * 8.5. PERMISSION SECTION (Approval UI)\n * ============================================ */\n\n.jp-Permission-section {\n  margin: 10px;\n  background: linear-gradient(145deg, #fffbeb, #fef3c7);\n  border-radius: 8px;\n  border: 1px solid #fbbf24;\n  overflow: hidden;\n  animation: jp-permission-fadeIn 0.3s ease;\n}\n\n@keyframes jp-permission-fadeIn {\n  from {\n    opacity: 0;\n    transform: translateY(-8px);\n  }\n  to {\n    opacity: 1;\n    transform: translateY(0);\n  }\n}\n\n.jp-Permission-section-header {\n  display: flex;\n  align-items: center;\n  gap: 8px;\n  padding: 10px 12px;\n  background: linear-gradient(90deg, rgba(251, 191, 36, 0.2) 0%, transparent 100%);\n  border-bottom: 1px solid rgba(251, 191, 36, 0.3);\n}\n\n.jp-Permission-section-icon {\n  width: 14px;\n  height: 14px;\n  color: #92400e;\n  display: flex;\n  align-items: center;\n  justify-content: center;\n}\n\n.jp-Permission-section-icon svg {\n  width: 14px;\n  height: 14px;\n}\n\n.jp-Permission-section-title {\n  font-size: 11px;\n  font-weight: 600;\n  color: #92400e;\n  text-transform: uppercase;\n  letter-spacing: 0.5px;\n  flex: 1;\n}\n\n.jp-Permission-section-count {\n  font-size: 10px;\n  background: #fbbf24;\n  color: #fff;\n  padding: 2px 6px;\n  border-radius: 10px;\n  font-weight: 600;\n  min-width: 18px;\n  text-align: center;\n}\n\n.jp-Permission-section-items {\n  background: #fff;\n}\n\n.jp-Permission-section-item {\n  display: flex;\n  align-items: flex-start;\n  gap: 10px;\n  padding: 12px;\n  background: #fff;\n  transition: background 0.15s ease;\n  flex-wrap: wrap;\n}\n\n.jp-Permission-section-item + .jp-Permission-section-item {\n  border-top: 1px solid #fef3c7;\n}\n\n.jp-Permission-section-item:hover {\n  background: rgba(254, 243, 199, 0.3);\n}\n\n.jp-Permission-section-item-content {\n  flex: 1 1 200px;\n  min-width: 200px;\n}\n\n.jp-Permission-section-item-title {\n  font-size: 13px;\n  font-weight: 500;\n  color: #24292f;\n  white-space: nowrap;\n  overflow: hidden;\n  text-overflow: ellipsis;\n}\n\n.jp-Permission-section-item-desc {\n  font-size: 11px;\n  color: #8b949e;\n  margin-top: 2px;\n}\n\n.jp-Permission-section-item-meta {\n  margin-top: 4px;\n  display: grid;\n  gap: 2px;\n}\n\n.jp-Permission-preview {\n  margin-top: 6px;\n  padding: 6px 8px;\n  border-radius: 6px;\n  border: 1px solid #e1e4e8;\n  background: #f6f8fa;\n  font-family: var(--jp-code-font-family);\n  font-size: 11px;\n  line-height: 1.4;\n  white-space: pre-wrap;\n  max-height: 180px;\n  overflow: auto;\n}\n\n.jp-Permission-preview--payload {\n  background: #fff;\n}\n\n.jp-Permission-details {\n  margin-top: 6px;\n}\n\n.jp-Permission-details summary {\n  font-size: 11px;\n  color: #57606a;\n  cursor: pointer;\n}\n\n.jp-Permission-section-actions {\n  display: flex;\n  gap: 6px;\n  flex-shrink: 0;\n  margin-left: auto;\n}\n\n/* Permission Buttons */\n.jp-Permission-btn {\n  padding: 6px 12px;\n  border-radius: 6px;\n  font-size: 12px;\n  font-weight: 500;\n  cursor: pointer;\n  transition: all 0.15s ease;\n  border: 1px solid transparent;\n  white-space: nowrap;\n}\n\n.jp-Permission-btn:disabled {\n  opacity: 0.6;\n  cursor: not-allowed;\n}\n\n.jp-Permission-btn--skip {\n  background: transparent;\n  color: #57606a;\n  border-color: #d0d7de;\n}\n\n.jp-Permission-btn--skip:hover:not(:disabled) {\n  background: #f6f8fa;\n  border-color: #8b949e;\n}\n\n.jp-Permission-btn--allow {\n  background: #f6f8fa;\n  color: #24292f;\n  border-color: #d0d7de;\n}\n\n.jp-Permission-btn--allow:hover:not(:disabled) {\n  background: #eaeef2;\n  border-color: #8b949e;\n}\n\n.jp-Permission-btn--always {\n  background: linear-gradient(180deg, #2da44e 0%, #26903f 100%);\n  color: #fff;\n  border-color: #238636;\n}\n\n.jp-Permission-btn--always:hover:not(:disabled) {\n  background: linear-gradient(180deg, #2ea44f 0%, #22863a 100%);\n}\n\n.jp-Permission-btn--modify {\n  background: #fff8c5;\n  color: #9a6700;\n  border-color: #d4a72c;\n}\n\n.jp-Permission-btn--modify:hover:not(:disabled) {\n  background: #fef3c7;\n  border-color: #b08800;\n}\n\n.jp-Permission-btn--reject {\n  background: transparent;\n  color: #cf222e;\n  border-color: #cf222e;\n}\n\n.jp-Permission-btn--reject:hover:not(:disabled) {\n  background: rgba(207, 34, 46, 0.1);\n}\n\n.jp-Permission-btn--cancel {\n  background: transparent;\n  color: #57606a;\n  border-color: #d0d7de;\n  padding: 6px 8px;\n  font-size: 14px;\n}\n\n.jp-Permission-btn--cancel:hover:not(:disabled) {\n  background: #f6f8fa;\n  border-color: #8b949e;\n}\n\n/* Dark theme overrides for Permission Section */\n[data-jp-theme-light="false"] .jp-Permission-section {\n  background: linear-gradient(145deg, rgba(251, 191, 36, 0.1), rgba(251, 191, 36, 0.05));\n  border-color: rgba(251, 191, 36, 0.4);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-section-header {\n  background: linear-gradient(90deg, rgba(251, 191, 36, 0.15) 0%, transparent 100%);\n  border-bottom-color: rgba(251, 191, 36, 0.2);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-section-icon {\n  color: #fbbf24;\n}\n\n[data-jp-theme-light="false"] .jp-Permission-section-title {\n  color: #fbbf24;\n}\n\n[data-jp-theme-light="false"] .jp-Permission-section-count {\n  background: rgba(251, 191, 36, 0.8);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-section-items {\n  background: var(--jp-layout-color1);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-section-item {\n  background: var(--jp-layout-color1);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-section-item + .jp-Permission-section-item {\n  border-top-color: rgba(251, 191, 36, 0.15);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-section-item:hover {\n  background: rgba(251, 191, 36, 0.1);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-section-item-title {\n  color: var(--jp-ui-font-color0);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-section-item-desc {\n  color: var(--jp-ui-font-color2);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-preview {\n  border-color: var(--jp-border-color2);\n  background: var(--jp-layout-color2);\n  color: var(--jp-ui-font-color1);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-preview--payload {\n  background: var(--jp-layout-color1);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-details summary {\n  color: var(--jp-ui-font-color2);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-btn--skip {\n  color: var(--jp-ui-font-color1);\n  border-color: var(--jp-border-color1);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-btn--skip:hover:not(:disabled) {\n  background: var(--jp-layout-color2);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-btn--allow {\n  background: var(--jp-layout-color2);\n  color: var(--jp-ui-font-color0);\n  border-color: var(--jp-border-color1);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-btn--allow:hover:not(:disabled) {\n  background: var(--jp-layout-color3);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-btn--always {\n  background: linear-gradient(180deg, #238636 0%, #196c2e 100%);\n  border-color: #238636;\n}\n\n[data-jp-theme-light="false"] .jp-Permission-btn--modify {\n  background: rgba(154, 103, 0, 0.2);\n  color: #d29922;\n  border-color: rgba(154, 103, 0, 0.4);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-btn--reject {\n  color: #f85149;\n  border-color: #f85149;\n}\n\n[data-jp-theme-light="false"] .jp-Permission-btn--reject:hover:not(:disabled) {\n  background: rgba(248, 81, 73, 0.1);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-btn--cancel {\n  color: var(--jp-ui-font-color1);\n  border-color: var(--jp-border-color1);\n}\n\n[data-jp-theme-light="false"] .jp-Permission-btn--cancel:hover:not(:disabled) {\n  background: var(--jp-layout-color2);\n}\n\n/* ============================================\n * 9. ERROR DISPLAY\n * ============================================ */\n\n.jp-AIAgentPanel-error {\n  display: flex;\n  align-items: center;\n  gap: var(--jp-ai-space-2);\n  padding: var(--jp-ai-space-3);\n  background: var(--jp-ai-error-bg);\n  border: 1px solid var(--jp-ai-error-border);\n  border-radius: var(--jp-ai-radius-md);\n  color: var(--jp-ai-error-primary);\n  font-size: var(--jp-ai-font-size-sm);\n}\n\n/* ============================================\n * 10. INPUT AREA\n * ============================================ */\n\n.jp-AIAgentPanel-input {\n  display: flex;\n  flex-direction: column;\n  gap: var(--jp-ai-space-2);\n  padding: var(--jp-ai-space-4);\n  border-top: 1px solid var(--jp-border-color3);\n  background: var(--jp-layout-color1);\n  flex-shrink: 0;\n}\n\n.jp-AIAgentPanel-controls {\n  display: flex;\n  align-items: center;\n  gap: var(--jp-ai-space-2);\n}\n\n.jp-AIAgentPanel-thinkingToggle {\n  display: inline-flex;\n  align-items: center;\n  gap: var(--jp-ai-space-2);\n  font-size: var(--jp-ai-font-size-xs);\n  color: var(--jp-ui-font-color2);\n}\n\n.jp-AIAgentPanel-inputRow {\n  display: flex;\n  gap: var(--jp-ai-space-2);\n  align-items: flex-end;\n}\n\n.jp-AIAgentPanel-textarea {\n  flex: 1;\n  min-height: 40px;\n  max-height: 200px;\n  padding: var(--jp-ai-space-2) var(--jp-ai-space-3);\n  border: 1px solid var(--jp-border-color2);\n  border-radius: var(--jp-ai-radius-md);\n  background: var(--jp-layout-color0);\n  color: var(--jp-ui-font-color1);\n  font-family: var(--jp-ui-font-family);\n  font-size: var(--jp-ai-font-size-md);\n  line-height: 1.5;\n  resize: none;\n  box-sizing: border-box;\n  transition: border-color var(--jp-ai-duration-fast) var(--jp-ai-ease),\n              box-shadow var(--jp-ai-duration-fast) var(--jp-ai-ease);\n}\n\n.jp-AIAgentPanel-textarea:focus {\n  outline: none;\n  border-color: var(--jp-brand-color1);\n  box-shadow: 0 0 0 3px rgba(13, 110, 253, 0.15);\n}\n\n.jp-AIAgentPanel-textarea::placeholder {\n  color: var(--jp-ui-font-color3);\n}\n\n.jp-AIAgentPanel-textarea:disabled {\n  opacity: 0.5;\n  cursor: not-allowed;\n}\n\n.jp-AIAgentPanel-sendButton {\n  align-self: flex-end;\n  width: 40px;\n  height: 40px;\n  padding: 0;\n  border: none;\n  border-radius: var(--jp-ai-radius-md);\n  background: var(--jp-brand-color1);\n  color: white;\n  cursor: pointer;\n  display: flex;\n  align-items: center;\n  justify-content: center;\n  transition: all var(--jp-ai-duration-fast) var(--jp-ai-ease);\n}\n\n.jp-AIAgentPanel-sendButton:hover:not(:disabled) {\n  background: var(--jp-brand-color0);\n  transform: translateY(-1px);\n  box-shadow: var(--jp-ai-shadow-md);\n}\n\n.jp-AIAgentPanel-sendButton:active:not(:disabled) {\n  transform: translateY(0);\n}\n\n.jp-AIAgentPanel-sendButton:disabled {\n  opacity: 0.5;\n  cursor: not-allowed;\n}\n\n/* Thinking Indicator Animation - rotating 200 phrases with 5 animation styles */\n.jp-AIAgentPanel-thinkingIndicator {\n  display: flex;\n  align-items: center;\n  justify-content: flex-start;\n  gap: var(--jp-ai-space-2);\n  padding: var(--jp-ai-space-2) 0;\n  margin: var(--jp-ai-space-2) 0;\n}\n\n.jp-AIAgentPanel-thinkingIndicator .jp-GatheringStorm {\n  display: block;\n  border-radius: 50%;\n  flex-shrink: 0;\n}\n\n.jp-AIAgentPanel-thinkingIndicator-text {\n  display: flex;\n  align-items: baseline;\n  font-size: var(--jp-ai-font-size-sm);\n  color: var(--jp-ui-font-color2);\n  font-style: italic;\n}\n\n.jp-AIAgentPanel-thinkingIndicator-wordContainer {\n  display: inline-flex;\n  align-items: baseline;\n  perspective: 400px;\n}\n\n.jp-AIAgentPanel-thinkingIndicator-wordWrapper {\n  position: relative;\n  display: inline-block;\n}\n\n/* Base word styling */\n.jp-thinking-word {\n  display: inline-block;\n  white-space: nowrap;\n  backface-visibility: hidden;\n}\n\n/* ===== Fade Up/Down Animation ===== */\n.jp-thinking-word--fade-exit {\n  position: absolute;\n  left: 0;\n  top: 0;\n  transition: opacity 0.3s ease, transform 0.3s ease;\n  opacity: 0;\n  transform: translateY(-15px);\n}\n\n.jp-thinking-word--fade-enter {\n  transition: opacity 0.3s ease, transform 0.3s ease;\n  opacity: 0;\n  transform: translateY(15px);\n}\n\n.jp-thinking-word--fade-active {\n  transition: opacity 0.3s ease, transform 0.3s ease;\n  opacity: 1;\n  transform: translateY(0);\n}\n\n/* ===== Card Rotator Animation (3D flip) ===== */\n.jp-thinking-word--card-exit {\n  position: absolute;\n  left: 0;\n  top: 0;\n  transition: transform 0.6s ease, opacity 0.6s ease;\n  transform: rotateX(90deg);\n  opacity: 0;\n}\n\n.jp-thinking-word--card-enter {\n  transition: transform 0.6s ease, opacity 0.6s ease;\n  transform: rotateX(-90deg);\n  opacity: 0;\n}\n\n.jp-thinking-word--card-active {\n  transition: transform 0.6s ease, opacity 0.6s ease;\n  transform: rotateX(0deg);\n  opacity: 1;\n}\n\n/* ===== Typewriter Animation ===== */\n.jp-thinking-word--typewriter::after {\n  content: \'|\';\n  color: var(--jp-brand-color1);\n  animation: jp-ai-cursor-blink 0.8s infinite;\n  margin-left: 2px;\n}\n\n.jp-thinking-word--typewriter-complete::after {\n  display: none;\n}\n\n@keyframes jp-ai-cursor-blink {\n  0%, 50% { opacity: 1; }\n  51%, 100% { opacity: 0; }\n}\n\n/* ===== Scramble Animation ===== */\n.jp-thinking-word--scramble {\n  font-family: \'Courier New\', monospace;\n  letter-spacing: 1px;\n}\n\n/* ===== Glitch Animation ===== */\n.jp-thinking-word--glitching {\n  animation: jp-ai-glitch 0.3s ease-in-out;\n}\n\n@keyframes jp-ai-glitch {\n  0% { transform: translate(0); filter: hue-rotate(0deg); }\n  20% { transform: translate(-3px, 3px); filter: hue-rotate(90deg); }\n  40% { transform: translate(3px, -3px); filter: hue-rotate(180deg); }\n  60% { transform: translate(-3px, -3px); filter: hue-rotate(270deg); }\n  80% { transform: translate(3px, 3px); filter: hue-rotate(360deg); }\n  100% { transform: translate(0); filter: hue-rotate(0deg); }\n}\n\n/* Animated bouncing dots */\n.jp-AIAgentPanel-thinkingIndicator-dots {\n  display: inline-flex;\n  gap: 1px;\n}\n\n.jp-AIAgentPanel-thinkingIndicator-dots span {\n  display: inline-block;\n  animation: jp-ai-dot-bounce 1.4s ease-in-out infinite;\n  font-weight: bold;\n}\n\n.jp-AIAgentPanel-thinkingIndicator-dots span:nth-child(1) {\n  animation-delay: 0s;\n}\n\n.jp-AIAgentPanel-thinkingIndicator-dots span:nth-child(2) {\n  animation-delay: 0.2s;\n}\n\n.jp-AIAgentPanel-thinkingIndicator-dots span:nth-child(3) {\n  animation-delay: 0.4s;\n}\n\n@keyframes jp-ai-dot-bounce {\n  0%, 60%, 100% {\n    transform: translateY(0);\n  }\n  30% {\n    transform: translateY(-4px);\n  }\n}\n\n/* ============================================\n * 11. SPINNER UTILITY\n * ============================================ */\n\n.jp-ai-spinner {\n  animation: jp-ai-spin 1s linear infinite;\n}\n\n/* ============================================\n * 12. SCROLLBAR STYLING\n * ============================================ */\n\n.jp-AIAgentPanel-messages::-webkit-scrollbar,\n.jp-AIAgentPanel-thinkingBlock-content::-webkit-scrollbar {\n  width: 6px;\n}\n\n.jp-AIAgentPanel-messages::-webkit-scrollbar-track,\n.jp-AIAgentPanel-thinkingBlock-content::-webkit-scrollbar-track {\n  background: transparent;\n}\n\n.jp-AIAgentPanel-messages::-webkit-scrollbar-thumb,\n.jp-AIAgentPanel-thinkingBlock-content::-webkit-scrollbar-thumb {\n  background: var(--jp-border-color2);\n  border-radius: 3px;\n}\n\n.jp-AIAgentPanel-messages::-webkit-scrollbar-thumb:hover,\n.jp-AIAgentPanel-thinkingBlock-content::-webkit-scrollbar-thumb:hover {\n  background: var(--jp-border-color1);\n}\n\n/* ============================================\n * TASK LIST (PLAN-STYLE)\n * ============================================ */\n\n.jp-TaskListBlock {\n  position: relative;\n}\n\n.jp-TaskListBlock-body {\n  position: relative;\n}\n\n.jp-TaskList-list {\n  list-style: none;\n  margin: 0;\n  padding: 0;\n  display: flex;\n  flex-direction: column;\n  gap: 6px;\n}\n\n.jp-TaskList-item {\n  display: flex;\n  align-items: flex-start;\n  gap: 8px;\n  padding: 6px 8px;\n  border-radius: 6px;\n  transition: background 0.2s ease, opacity 0.2s ease;\n}\n\n.jp-TaskList-item--active {\n  background: var(--jp-layout-color2);\n  box-shadow: inset 2px 0 0 var(--jp-brand-color1);\n}\n\n.jp-TaskList-item--done {\n  opacity: 0.75;\n}\n\n.jp-TaskList-item-icon {\n  display: flex;\n  align-items: center;\n  justify-content: center;\n  width: 18px;\n  height: 18px;\n  flex-shrink: 0;\n  color: var(--jp-ui-font-color3);\n}\n\n.jp-TaskList-item--active .jp-TaskList-item-icon {\n  color: var(--jp-brand-color1);\n}\n\n.jp-TaskList-item--done .jp-TaskList-item-icon {\n  color: var(--jp-success-color1);\n}\n\n.jp-TaskList-item--blocked .jp-TaskList-item-icon {\n  color: var(--jp-warn-color1, #9a6700);\n}\n\n.jp-TaskList-item--skipped .jp-TaskList-item-icon {\n  color: var(--jp-ui-font-color2);\n}\n\n.jp-TaskList-item-textContainer {\n  display: flex;\n  flex-direction: column;\n  gap: 2px;\n}\n\n.jp-TaskList-item-text {\n  position: relative;\n  font-size: 13px;\n  color: var(--jp-ui-font-color1);\n  line-height: 1.5;\n  display: inline-block;\n}\n\n.jp-TaskList-item-text::after {\n  content: \'\';\n  position: absolute;\n  left: 0;\n  right: 0;\n  top: 50%;\n  height: 1.5px;\n  background: var(--jp-border-color2);\n  transform: scaleX(0);\n  transform-origin: left;\n  transition: transform 0.3s ease;\n}\n\n.jp-TaskList-item--done .jp-TaskList-item-text::after {\n  transform: scaleX(1);\n}\n\n.jp-TaskList-item--done .jp-TaskList-item-text {\n  color: var(--jp-ui-font-color2);\n}\n\n.jp-TaskList-item-reason {\n  font-size: 11px;\n  color: var(--jp-ui-font-color3);\n}\n\n.jp-TaskList-item[data-status="completed"] .jp-TaskList-item-icon svg {\n  animation: jp-TaskList-check 0.3s ease;\n}\n\n@keyframes jp-TaskList-check {\n  0% {\n    transform: scale(0.8);\n  }\n  70% {\n    transform: scale(1.1);\n  }\n  100% {\n    transform: scale(1);\n  }\n}\n\n.jp-TaskList-spinner {\n  animation: jp-TaskList-spin 1s linear infinite;\n}\n\n@keyframes jp-TaskList-spin {\n  from {\n    transform: rotate(0deg);\n  }\n  to {\n    transform: rotate(360deg);\n  }\n}\n\n.jp-TaskList-leaf {\n  display: flex;\n  justify-content: center;\n  margin-top: 10px;\n  color: var(--jp-brand-color1);\n  opacity: 0.35;\n}\n\n@media (prefers-reduced-motion: reduce) {\n  .jp-TaskList-spinner {\n    animation: none;\n  }\n\n  .jp-TaskList-item-text::after,\n  .jp-TaskList-item {\n    transition: none;\n  }\n\n  .jp-TaskList-item[data-status="completed"] .jp-TaskList-item-icon svg {\n    animation: none;\n  }\n}\n',""]);const p=r}}]);