#run with python -m unittest test.test_baccoemu
import unittest
import baccoemu
import numpy as np
import copy

emu = baccoemu.Matter_powerspectrum()
emu_Arico23_Burger25 = baccoemu.Matter_powerspectrum(nonlinear_model_name='Arico2023',baryonic_model_name='Burger2025')
emu_bispectrum= baccoemu.Matter_bispectrum()
emu_bao = baccoemu.Matter_powerspectrum(smeared_bao=True, no_wiggles=False)
emu_compute_nowiggles = baccoemu.Matter_powerspectrum(smeared_bao=False, no_wiggles=False)
lbias_emu = baccoemu.Lbias_expansion()
lbias_RSD_emu = baccoemu.Lbias_expansion_RSD()

kk = np.logspace(-2, 0.6, 10)
k_lbias = np.logspace(-2, np.log10(0.7), 10)

params = {
    'omega_matter'  :  0.315,
    'A_s'           :  2e-9,
    'omega_baryon'  :  0.05,
    'ns'            :  0.96,
    'hubble'        :  0.67,
    'neutrino_mass' :  0.1,
    'w0'            : -1.1,
    'wa'            :  0.2,
    'expfactor'     :  0.8,

    'M_c'           :  14,
    'eta'           : -0.3,
    'beta'          : -0.22,
    'M1_z0_cen'     : 10.5,
    'theta_out'     : 0.25,
    'theta_inn'     : -0.86,
    'M_inn'         : 13.4
}

params_sigma8 = copy.deepcopy(params)
del params_sigma8['A_s']
params_sigma8['sigma8_cold'] = 0.78

params_vec = copy.deepcopy(params)
for key in params_vec:
    params_vec[key] = np.full(3, params_vec[key])

params_sigma8_vec = copy.deepcopy(params_sigma8)
for key in params_sigma8_vec:
    params_sigma8_vec[key] = np.full(3, params_sigma8_vec[key])


params_sigma8_cold = copy.deepcopy(params)
params_sigma8_cold['sigma8_cold'] = 0.78
params_sigma8_cold['omega_cold'] = params['omega_matter']
del params_sigma8_cold['A_s']
del params_sigma8_cold['omega_matter']

params_vec_cold = copy.deepcopy(params_sigma8_cold)
for key in params_vec_cold:
    params_vec_cold[key] = np.full(3, params_vec_cold[key])

dec_prec = 3
dec_prec_vec = 3

class test_baccoemu(unittest.TestCase):
    def test_get_nonlinear_boost(self):
        k, Q = emu.get_nonlinear_boost(k=kk, **params)
        k, Q_vec = emu.get_nonlinear_boost(k=kk, **params_vec)
        Q_tab = np.array([1., 0.99590761, 0.99121246, 0.98290575, 1.03702336,
                          1.33282697, 2.278292, 5.05824684, 11.3084095, 20.30722402])
        for i, j in zip(Q, Q_tab):
            self.assertAlmostEqual(np.round(i / j, dec_prec), 1)
        for i, j in zip(Q_vec[0], Q_tab):
            self.assertAlmostEqual(np.round(i / j, dec_prec), 1)

