    def test_get_baryonic_boost_bispectrum(self):
        k, S, _ = emu_bispectrum.get_baryonic_boost(k1=kk,k2=kk,k3=kk, **params_sigma8_cold)
        k, S_vec, _ = emu_bispectrum.get_baryonic_boost(k1=kk,k2=kk,k3=kk, **params_vec_cold)
        S_tab = np.array([1.0005705 , 1.000588  , 1.0004864 , 0.99971706, 0.9959934 ,
                          0.98116356, 0.9436257 , 0.89987946, 0.83730465, 0.73122686])
        for i, j in zip(S, S_tab):
            self.assertAlmostEqual(np.round(i / j, dec_prec), 1)
        for i, j in zip(S_vec[0], S_tab):
            self.assertAlmostEqual(np.round(i / j, dec_prec), 1)
        wrong_pars = copy.deepcopy(params_sigma8_cold)
        del wrong_pars['omega_cold']
        with self.assertRaises(KeyError):
            _ = emu_bispectrum.get_baryonic_boost(k1=kk,k2=kk,k3=kk, **wrong_pars)
        wrong_pars = copy.deepcopy(params_sigma8_cold)
        del wrong_pars['sigma8_cold']
        with self.assertRaises(KeyError):
            _ = emu_bispectrum.get_baryonic_boost(k1=kk,k2=kk,k3=kk, **wrong_pars)
        wrong_pars = copy.deepcopy(params_sigma8_cold)

