    def test_baryon_factions(self):
        values_from_bacco = {
            'ej_gas': np.array([0.12111896, 0.11832026, 0.07736338, 0.03138011]),
            'cen_galaxy': np.array([0.02589441, 0.00804084, 0.0021568 , 0.00052424]),
            'sat_galaxy': np.array([0.00416425, 0.00282301, 0.00184659, 0.00116087]),
            'bo_gas': np.array([0.00755254, 0.02954594, 0.07735565, 0.12535425]),
            're_gas': np.array([1.2111895e-09, 1.1832014e-07, 7.7355644e-06, 3.1069422e-04]),
        }
        M200c = np.logspace(12, 15, 4)
        fb = baccoemu.get_baryon_fractions(M200c, **params)
        for key in values_from_bacco.keys():
            self.assertEqual(len(fb[key]), len(M200c))
            for i, j in zip(fb[key], values_from_bacco[key]):
                if key == 're_gas':
                  self.assertAlmostEqual(np.round(i - j, dec_prec), 0)
                else:
                  self.assertAlmostEqual(np.round(i / j, dec_prec), 1)
        wrong_pars = copy.deepcopy(params)
        wrong_pars['M_c'] = 15.0
        wrong_pars['M1_z0_cen'] = 14.5
        # all_args = {'M200c': M200c, wrong_pars}
        with self.assertRaises(ValueError):
          baccoemu.get_baryon_fractions(M200c, **wrong_pars)

