#!/usr/bin/env python
"""Debug script to see raw AVPs from the diameter message"""
import binascii
from diameter.message import Message

hex_str = ""

data = binascii.unhexlify(hex_str)
msg = Message.from_bytes(data)

print(f"Message type: {type(msg)}")
print(f"Has _avps: {hasattr(msg, '_avps')}")

if hasattr(msg, '_avps'):
    print(f"Number of AVPs in _avps: {len(msg._avps)}")
    for i, avp in enumerate(msg._avps):
        print(f"  AVP {i}: code={avp.code}, vendor={avp.vendor_id}, len={avp.length if hasattr(avp, 'length') else 'N/A'}")
else:
    print("No _avps attribute")

# Check attributes
attrs = [k for k in vars(msg) if not k.startswith("_") and k != "header"]
print(f"\nNumber of attributes: {len(attrs)}")
for attr in attrs:
    val = getattr(msg, attr)
    print(f"  {attr}: {type(val).__name__}")
