def find_country_insee_code(country_name):
    """
    Retrieve a country code from a country name.
    """
    country_name = country_name.upper()
    for country in COUNTRIES:
        if country["name"] == country_name:
            return country["code_insee"]
    return None


def find_country_by_insee_code(insee_code: str) -> Optional[dict]:
    """
    Retrieve a country details from its INSEE code.
    """
    for country in COUNTRIES:
        if country["code_insee"] == insee_code:
            return country
    return None
