def test_nd_kkf_mat_gen_xz_fill_mat_xx(gen_xz: NdKkfMatGenXz) -> None:
    """."""
    mat_zz = np.linspace(1, 14 * 14, num=14 * 14).reshape((14, 14))
    mat_xx = 404 * np.ones((gen_xz.gen_x.num_x, gen_xz.gen_x.num_x))
    gen_xz.fill_mat_xx(mat_zz, mat_xx)
    assert mat_xx == pytest.approx(np.array(REF))


def test_easy_fill_mat_xx(gen_xz: NdKkfMatGenXz) -> None:
    """."""
    mat_zz = np.linspace(1, 14 * 14, num=14 * 14).reshape((14, 14))
    mat_xx = np.dot(gen_xz.h_mat.T, np.dot(mat_zz, gen_xz.h_mat))
    ref0 = np.array(REF)
    ref0[ref0 > 403] = 0.0
    assert mat_xx == pytest.approx(ref0)
