import locale

def new_text_id():
    """Generates unique text IDs like #TEXT1#, #TEXT2#, etc."""
    if not hasattr(new_text_id, "count"):
        new_text_id.count = 0
    new_text_id.count += 1
    return f"#TEXT_{new_text_id.count}#"

