spy_js = r'''
const slug = (s) => s.toLowerCase().replace(/[^a-z0-9\s-]/g, '').replace(/\s+/g, '-').replace(/-+/g, '-');
const frag = document.createDocumentFragment();

document.querySelector('%s').querySelectorAll('%s').forEach((h,i) => {
  const text = h.textContent.trim();
  const id = slug(text)+'_'+i; h.id = id;
  const li = document.createElement('li'), a = document.createElement('a');
  const l = parseInt(h.tagName[1]) || 6;
  li.className = `[&.uk-active]:bg-[hsl(var(--primary)/0.4)] rounded-sm text-sm`;
  a.href = '#'+id; a.textContent = text;
  li.style.paddingLeft = `${(l - 1) * 0.75}rem`;
  a.className = '!line-clamp-1';
  li.append(a); frag.append(li);
});
document.getElementById('%s').append(frag);
'''

spy_scrolling = '''
document.body.addEventListener('active', ({detail}) => {
    const target = detail[1];
    const {y,height} = target.getBoundingClientRect();
    if (y > height && y < innerHeight-height) return;
    requestAnimationFrame(() => target.scrollIntoView({block: 'center',container: 'nearest'}));
}, {passive: true})
'''

