def ScrollSpy(headings=[1,2,3,4,5],       # Heading levels to map section/links from
              target_sel:str='body',      # Container to render scrollspy links from
              offset:int=0,               # Offset from top of viewport to set section as active
              nav_id:str='scrollspy-nav', # ID given to the containing Ul component 
              smooth_scroll:bool=False,   # Enable smooth-scrolling to headings
              auto_scroll:bool=False,     # Scroll active links into view if they're not in the viewport
              cls:str='',
              **kwargs
              )->FT:
    "Standalone Scrollspy nav menu, mapping heading tags from target into link"
    js = spy_js % (target_sel, ','.join([f'h{i}' for i in headings]), nav_id)
    if auto_scroll: js+=spy_scrolling
    nav_args = f'closest: li;offset: {offset};scroll: {str(smooth_scroll).lower()};'
    return Ul(Script(Safe(js), type='module'), data_uk_scrollspy_nav=nav_args,
                      id=nav_id, cls=f'uk-nav uk-nav-default {cls}', **kwargs)

