def progressbar(p):
    """Выводит один прогресс-бар в консоль"""
    # Делаем процент от 0 до 100
    p = min(100, max(0, p))

    # Считаем сколько символов заполнить (всего 50 символов)
    filled = int(p / 2)  # 2% на один символ
    empty = 50 - filled

    # Рисуем прогресс-бар
    bar = '█' * filled + '░' * empty

    # Выводим
    print(f'\r[{bar}] {p:.1f}%', end='')

    # Если 100%, переходим на новую строку
    if p >= 100:
        print()