# Configuration file for the Sphinx documentation builder.
#
# For the full list of built-in configuration values, see the documentation:
# https://www.sphinx-doc.org/en/master/usage/configuration.html

# -- Project information -----------------------------------------------------

project = "rapsqlite"
copyright = "2026, RAP Project"
author = "RAP Project"
release = "0.2.0"
version = "0.2.0"

# -- General configuration ----------------------------------------------------

extensions = [
    "sphinx.ext.autodoc",
    "sphinx.ext.napoleon",  # Google/NumPy style docstrings
    "sphinx.ext.viewcode",
    "sphinx.ext.autosummary",
]

# Napoleon settings for Google-style docstrings
napoleon_google_docstring = True
napoleon_numpy_docstring = False
napoleon_include_init_with_doc = False
napoleon_include_private_with_doc = False
napoleon_include_special_with_doc = True
napoleon_use_admonition_for_examples = False
napoleon_use_admonition_for_notes = False
napoleon_use_admonition_for_references = False
napoleon_use_ivar = False
napoleon_use_param = True
napoleon_use_rtype = True
napoleon_preprocess_types = False
napoleon_type_aliases = None
napoleon_attr_annotations = True

# Autodoc settings
autodoc_default_options = {
    "members": True,
    "member-order": "bysource",
    "special-members": "__init__,__aenter__,__aexit__",
    "undoc-members": False,
    "exclude-members": "__weakref__",
    "show-inheritance": True,
}

autodoc_member_order = "bysource"
autoclass_content = "both"  # Include both class and __init__ docstrings

# Don't mock rapsqlite - Read the Docs installs this repo (builds extension)
autodoc_mock_imports = []

# Templates
templates_path = ["_templates"]

# The suffix(es) of source filenames
source_suffix = {
    ".rst": "restructuredtext",
}

# The master toctree document
master_doc = "index"

# The language for content autogenerated by Sphinx
language = "en"

# List of patterns to ignore when looking for source files
exclude_patterns = [
    "_build",
    "Thumbs.db",
    ".DS_Store",
    "*.pyc",
    "*.md",  # Exclude markdown files (converted to RST)
    "ADVANCED.md",
    "AIOSQLITE_*.md",
    "ASYNC_*.md",
    "BACKUP_*.md",
    "MIGRATION.md",
    "PLAN_*.md",
    "README_*.md",
    # Note: ROADMAP.md is kept intentionally
]

# -- Options for HTML output -------------------------------------------------

html_theme = "sphinx_rtd_theme"

html_theme_options = {
    "collapse_navigation": False,
    "sticky_navigation": True,
    "navigation_depth": 4,
    "includehidden": True,
    "titles_only": False,
    "logo_only": False,
    "prev_next_buttons_location": "bottom",
    "style_external_links": True,
}

html_static_path = ["_static"]

html_css_files = [
    "custom.css",
]

# -- Options for autosummary -------------------------------------------------
autosummary_generate = True

# -- Options for viewcode ---------------------------------------------------
viewcode_enable_epub = True
