"""
GNN Model Configuration.

Provides configuration schema, defaults, transformers, and validation
for the GNN machine learning model.

This module registers GNN-specific configuration components with the
ModelRegistry, enabling the core config system to remain model-agnostic.

PHASE 2 REFACTORING: This adapter now uses AutoGeneratedConfigAdapter
to auto-generate defaults and transformers from Pydantic Field declarations.
All defaults are maintained in GNNConfig (model_configs.py) for single
source of truth.
"""

from typing import Dict, Any
from symfluence.models.base import AutoGeneratedConfigAdapter
from symfluence.core.config.models.model_configs import GNNConfig


class GNNConfigAdapter(AutoGeneratedConfigAdapter):
    """
    Configuration adapter for GNN model with auto-generated defaults and transformers.

    Defaults and transformers are automatically extracted from GNNConfig
    Pydantic model. Only custom validation logic is implemented here.
    """

    def __init__(self, model_name: str = 'GNN'):
        super().__init__(model_name)

    def get_config_schema(self):
        """Return GNN Pydantic configuration schema."""
        return GNNConfig

    def validate(self, config: Dict[str, Any]) -> None:
        """Validate GNN-specific configuration."""
        pass  # Add custom validation logic if needed
