from adapters.token_adapter.adapter import TokenAdapter

# Initialize adapter (no config needed)
adapter = TokenAdapter()

# Handle missing parameters
try:
    success, data = await adapter.get_token()
    if not success:
        print(f"Error: {data}")
except Exception as e:
    print(f"Unexpected error: {e}")

# Handle API errors
success, data = await adapter.get_token_by_address("invalid-address")
if not success:
    print(f"API error: {data}")
else:
    print(f"Token found: {data}")
