async def balance_html_tags_async(txt, self_closing=None):
    result = txt
    try:
        if txt is None: return ''

        if self_closing is None:
            self_closing = {'br', 'img', 'hr', 'input', 'meta', 'link'}

        txt = re.sub(
            r'(<\s*[\w:-]+(?:\s[^<>]*)?)(?=[^>]*?(?:<|$))',
            r'\1>',
            txt,
            flags=re.DOTALL
        )

        parts = re.split(r'(<\/?[\w:-]+(?:\s[^<>]*?)?>)', txt, flags=re.DOTALL)
        tag_re = re.compile(r'^\s*<\s*(\/?)\s*([\w:-]+)(?:\s[^<>]*?)?>\s*$', re.DOTALL)

        stack = []
        out = []

        for p in parts:
            if not p: continue
            m = tag_re.match(p)
            if m:
                is_closing = bool(m.group(1))
                tag = m.group(2).lower()
                if is_closing:
                    if stack and stack[-1] == tag:
                        out.append(p)
                        stack.pop()
                    elif tag in stack:
                        # Ğ·Ğ°ĞºÑ€Ñ‹Ğ²Ğ°ĞµĞ¼ Ğ¿Ñ€Ğ¾Ğ¼ĞµĞ¶ÑƒÑ‚Ğ¾Ñ‡Ğ½Ñ‹Ğµ Ñ‚ĞµĞ³Ğ¸ Ğ´Ğ¾ Ğ½ÑƒĞ¶Ğ½Ğ¾Ğ³Ğ¾, Ğ·Ğ°Ñ‚ĞµĞ¼ ÑĞ°Ğ¼ Ñ‚ĞµĞ³
                        while stack and stack[-1] != tag:
                            t = stack.pop()
                            out.append(f'</{t}>')
                        out.append(p)
                        stack.pop()
                    else:
                        # Ğ»Ğ¸ÑˆĞ½Ğ¸Ğ¹ Ğ·Ğ°ĞºÑ€Ñ‹Ğ²Ğ°ÑÑ‰Ğ¸Ğ¹ â€” Ğ¿Ñ€Ğ¾Ğ¿ÑƒÑĞºĞ°ĞµĞ¼
                        continue
                else:
                    # Ğ¾Ñ‚ĞºÑ€Ñ‹Ğ²Ğ°ÑÑ‰Ğ¸Ğ¹ Ñ‚ĞµĞ³ (Ñ Ğ°Ñ‚Ñ€Ğ¸Ğ±ÑƒÑ‚Ğ°Ğ¼Ğ¸ ÑĞ¾Ñ…Ñ€Ğ°Ğ½ÑĞµĞ¼)
                    if tag in self_closing:
                        out.append(p)
                    else:
                        out.append(p)
                        stack.append(tag)
            else:
                out.append(p)

        while stack:
            t = stack.pop()
            out.append(f'</{t}>')

        await asyncio.sleep(0)
        result = ''.join(out)
    except Exception as e:
        logger.info(log_ % str(e))
        await asyncio.sleep(round(random.uniform(0, 1), 2))
    return result


