async def run_cmd(cmd0, timeout=None):
    """
    Ğ—Ğ°Ğ¿ÑƒÑĞºĞ°ĞµÑ‚ ĞºĞ¾Ğ¼Ğ°Ğ½Ğ´Ñƒ shell, Ğ¶Ğ´Ñ‘Ñ‚ stdout/stderr. ĞŸÑ€Ğ¸ timeout â€” ÑƒĞ±Ğ¸Ğ²Ğ°ĞµÑ‚ Ğ¿Ñ€Ğ¾Ñ†ĞµÑÑ.
    Ğ’Ğ¾Ğ·Ğ²Ñ€Ğ°Ñ‰Ğ°ĞµÑ‚ (rc, stdout_str, stderr_str). ĞŸÑ€Ğ¸ Ñ‚Ğ°Ğ¹Ğ¼Ğ°ÑƒÑ‚Ğµ Ğ²Ğ¾Ğ·Ğ²Ñ€Ğ°Ñ‰Ğ°ĞµÑ‚ rc=-1 Ğ¸ stderr Ñ Ñ‚ĞµĞºÑÑ‚Ğ¾Ğ¼ TIMEOUT.
    """
    proc = await asyncio.create_subprocess_shell(
        cmd0, stdout=asyncio.subprocess.PIPE, stderr=asyncio.subprocess.PIPE
    )
    try:
        if timeout is None:
            out_, err_ = await proc.communicate()
        else:
            out_, err_ = await asyncio.wait_for(proc.communicate(), timeout=timeout)
    except asyncio.TimeoutError:
        # Ñ‚Ğ°Ğ¹Ğ¼Ğ°ÑƒÑ‚ â€” ÑƒĞ±Ğ¸Ğ²Ğ°ĞµĞ¼ Ğ¿Ñ€Ğ¾Ñ†ĞµÑÑ Ğ¸ Ğ¶Ğ´Ñ‘Ğ¼ ĞµĞ³Ğ¾ Ğ·Ğ°Ğ²ĞµÑ€ÑˆĞµĞ½Ğ¸Ñ
        try:
            proc.kill()
        except Exception:
            pass
        try:
            await asyncio.wait_for(proc.wait(), timeout=5)
        except Exception:
            pass
        return -1, "", f"TIMEOUT after {timeout}s"
    rc = proc.returncode
    out_s = out_.decode(errors='ignore') if out_ else ""
    err_s = err_.decode(errors='ignore') if err_ else ""
    return rc, out_s, err_s


async def get_media_duration(path: str, ffprobe_path: str = "ffprobe", timeout: float = 3.0) -> float:
    if not path or not os.path.exists(path):
        return 0.0

    try:
        proc = await asyncio.create_subprocess_exec(
            ffprobe_path, "-v", "error",
            "-show_entries", "format=duration",
            "-of", "default=noprint_wrappers=1:nokey=1",
            path,
            stdout=asyncio.subprocess.PIPE,
            stderr=asyncio.subprocess.PIPE
        )
        try:
            stdout, _ = await asyncio.wait_for(proc.communicate(), timeout=timeout)
        except asyncio.TimeoutError:
            proc.kill()
            await proc.communicate()
            raise
        out = stdout.decode().strip()
        if out:
            try:
                val = float(out)
                if val > 0 and not (val == float("inf")):
                    return val
            except Exception:
                pass
    except Exception:
        pass

    try:
        from mutagen import File as MutagenFile  # type: ignore
        m = MutagenFile(path)
        if m is not None:
            dur = getattr(m.info, "length", None)
            if dur:
                return float(dur)
    except Exception:
        pass

    try:
        from pydub import AudioSegment  # type: ignore
        audio = AudioSegment.from_file(path)
        return len(audio) / 1000.0
    except Exception:
        pass

    try:
        import cv2  # type: ignore
        cap = cv2.VideoCapture(str(path))
        if cap is not None and cap.isOpened():
            frame_count = cap.get(cv2.CAP_PROP_FRAME_COUNT) or 0.0
            fps = cap.get(cv2.CAP_PROP_FPS) or 0.0
            cap.release()
            if fps > 0 and frame_count > 0:
                return float(frame_count) / float(fps)
    except Exception:
        pass

    return 0.0
